(20, 'Simplex Print', '[[strip]]\r\n\r\n[[* /* reset body background and color, just in case */ *]]\r\nbody {\r\n    background: #fff;\r\n    color: #000;\r\n    font-family: Georgia, Times New Roman, serif;\r\n    font-size: 12pt\r\n}\r\n[[* /* any element with class noprint or listed below should not be printed */ *]]\r\n.noprint,\r\n.visuallyhidden {\r\n    display: none\r\n}\r\n[[* /* display image as block */ *]]\r\nimg {\r\n    display: block;\r\n    float: none\r\n}\r\n[[* /* links arent clickable on paper, lets display url */ *]]\r\na:link:after {\r\n    content: \" (\" attr(href) \") \";\r\n}\r\na {\r\n    text-decoration: underline\r\n}\r\n\r\n[[/strip]]', 'Default Print style rules attached to Simplex Design', 'print', NULL, 1748240028, 1748240028);
